sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        // Parse the request body automatically by accessing the .data property
        var jsonData = request.body.data;

        // Validate required fields
        if (!jsonData.payload || !jsonData.correlation_id || !jsonData.type || !jsonData.target_table) {
            throw new Error('Missing required fields in JSON payload');
        }

        // Create and populate the record
        var taskRecord = new GlideRecord('x_1118332_servic_0_integration_task');
        taskRecord.initialize();
        taskRecord.setValue('payload', jsonData.payload);
        taskRecord.setValue('correlation_id', jsonData.correlation_id);
        taskRecord.setValue('type', jsonData.type);
        taskRecord.setValue('target_table', jsonData.target_table);

        var sysId = taskRecord.insert();

        if (!sysId) {
            throw new Error('Failed to create record');
        }

        // Set successful response
		var responseBody = { sys_id: sysId };
        response.setStatus(201);
        response.setBody(responseBody);
    } catch (e) {
        // Handle errors with appropriate status and message
        response.setStatus(400);  // Use 400 for bad requests, or 500 for server errors
        var errorBody = { error: e.message };
        response.setBody(errorBody);
    }
})(request, response);
